// DIFERENÇA DO == E ===

// O == verifica se o conteúdo é igual ao outro (COMPARAÇÃO DE CONTEÚDO)
// O === verifica se o conteúdo e o tipo são iguais (COMPARAÇÃO DE CONTEÚDO E TIPO DA VARIÁVEL)

// Exemplo:

let num1 = 3; // - INT
let num2 = "3" // - STRING

console.log(num1 == num2); // TRUE
console.log(num1 === num2); // FALSE